content.solution:
* {
  box-sizing: border-box;
}

.card {
  padding: 24px;
  margin-bottom: 16px;
  border: 2px solid #dee2e6;
  border-radius: 8px;
  background-color: #f8f9fa;
}

.card h3 {
  margin-top: 0;
  color: #212529;
}

.card p {
  margin-bottom: 0;
  color: #6c757d;
}
