.cards {
  display: flex;
  flex-wrap: wrap;
  gap: 16px;
}

.card {
  flex: 1 1 200px;
  padding: 16px;
  border: 1px solid #dee2e6;
  border-radius: 8px;
}

.card h3 {
  margin-top: 0;
}
