content.solution:
.layout {
  display: grid;
  grid-template-columns: 200px 1fr;
  grid-template-rows: 60px 1fr 40px;
  min-height: 300px;
  gap: 4px;
  grid-template-areas:
    "header  header"
    "sidebar main"
    "footer  footer";
}

.header  { grid-area: header;  background: #3498db; color: white; padding: 0 16px; display: flex; align-items: center; }
.sidebar { grid-area: sidebar; background: #ecf0f1; padding: 16px; }
.main    { grid-area: main;    background: #ffffff; padding: 16px; }
.footer  { grid-area: footer;  background: #2c3e50; color: white; padding: 0 16px; display: flex; align-items: center; }
