.navbar {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  background: #2c3e50;
  color: white;
  padding: 0 24px;
  height: 56px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  z-index: 100;
}

.navbar a {
  color: white;
  text-decoration: none;
  margin-left: 16px;
}

.content {
  margin-top: 56px;
  padding: 24px;
}
