/* Mobile primeiro */
.layout {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.sidebar {
  background: #f8f9fa;
  padding: 16px;
  border-radius: 8px;
}

.main {
  flex: 1;
  background: white;
  padding: 16px;
  border: 1px solid #dee2e6;
}

/* Tablet e desktop */
@media (min-width: 768px) {
  .layout {
    flex-direction: row;
  }

  .sidebar {
    width: 200px;
  }
}
