.titulo {
  font-size: 1.5rem;
  color: #2c3e50;
}

.titulo::after {
  content: '';
  display: block;
  width: 60px;
  height: 3px;
  background: #3498db;
  margin-top: 8px;
}

.badge {
  font-weight: bold;
  color: #e67e22;
}

.badge::before {
  content: '★ ';
}

li {
  padding: 10px 16px;
  list-style: none;
  transition: all 0.2s;
}

